import React, { useEffect, useState } from "react";
import axios from "axios";

const App = () => {
  const [holdings, setHoldings] = useState([]);
  const [selectedCoins, setSelectedCoins] = useState([]);
  const [initialGain, setInitialGain] = useState(0);
  const [harvestedGain, setHarvestedGain] = useState(0);

  useEffect(() => {
    const fetchHoldings = async () => {
      const response = await axios.get("/api/holdings"); // Replace with mock API or json-server
      setHoldings(response.data);
      calculateInitialGain(response.data);
    };
    fetchHoldings();
  }, []);

  const calculateInitialGain = (data) => {
    const gain = data.reduce((acc, item) => acc + item.stcg.gain + item.ltcg.gain, 0);
    setInitialGain(gain);
    setHarvestedGain(gain);
  };

  const handleToggle = (coin) => {
    let updatedSelection;
    if (selectedCoins.includes(coin.coin)) {
      updatedSelection = selectedCoins.filter((c) => c !== coin.coin);
    } else {
      updatedSelection = [...selectedCoins, coin.coin];
    }
    setSelectedCoins(updatedSelection);
    updateHarvestedGain(holdings, updatedSelection);
  };

  const updateHarvestedGain = (data, selected) => {
    const harvested = data
      .filter((item) => !selected.includes(item.coin))
      .reduce((acc, item) => acc + item.stcg.gain + item.ltcg.gain, 0);
    setHarvestedGain(harvested);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-3xl font-bold mb-4">Tax Loss Harvesting Tool</h1>
      <div className="mb-6 p-4 bg-white shadow rounded">
        <h2 className="text-xl font-semibold mb-2">Capital Gains Summary</h2>
        <div className="flex gap-6">
          <div>
            <p className="text-gray-600">Before Harvesting:</p>
            <p className="text-2xl font-bold text-red-500">
              â‚¹{initialGain.toFixed(2)}
            </p>
          </div>
          <div>
            <p className="text-gray-600">After Harvesting:</p>
            <p className="text-2xl font-bold text-green-500">
              â‚¹{harvestedGain.toFixed(2)}
            </p>
          </div>
        </div>
      </div>
      <div className="grid gap-4 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3">
        {holdings.map((coin, index) => (
          <div
            key={index}
            onClick={() => handleToggle(coin)}
            className={`cursor-pointer border p-4 rounded shadow-md transition-all duration-300 ${
              selectedCoins.includes(coin.coin) ? "bg-red-100 border-red-400" : "bg-white"
            }`}
          >
            <div className="flex items-center gap-3 mb-3">
              <img src={coin.logo} alt={coin.coinName} className="w-8 h-8" />
              <h3 className="font-semibold text-lg">{coin.coinName}</h3>
            </div>
            <p className="text-sm text-gray-700">
              <strong>Holding:</strong> {coin.totalHolding.toFixed(4)}
            </p>
            <p className="text-sm text-gray-700">
              <strong>STCG Gain:</strong> â‚¹{coin.stcg.gain.toFixed(2)}
            </p>
            <p className="text-sm text-gray-700">
              <strong>LTCG Gain:</strong> â‚¹{coin.ltcg.gain.toFixed(2)}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
