import React, { useState, useEffect } from 'react';
import { Sparkles } from 'lucide-react';

const holdingsData = [
  {
    coin: 'btc',
    coinName: 'Bitcoin',
    logo: 'https://cryptologos.cc/logos/bitcoin-btc-logo.png',
    totalHoldings: 2.5,
    averageBuyPrice: 30000,
    currentPrice: 35000,
    stcg: { gain: 10000, balance: 1.5 },
    ltcg: { gain: -5000, balance: 1 },
  },
  {
    coin: 'eth',
    coinName: 'Ethereum',
    logo: 'https://cryptologos.cc/logos/ethereum-eth-logo.png',
    totalHoldings: 5,
    averageBuyPrice: 1500,
    currentPrice: 1200,
    stcg: { gain: -3000, balance: 2 },
    ltcg: { gain: 2000, balance: 3 },
  },
];

const calculateNetGains = ({ stcg, ltcg }) => ({
  stcgNet: stcg.profits - stcg.losses,
  ltcgNet: ltcg.profits - ltcg.losses,
  realised: stcg.profits - stcg.losses + ltcg.profits - ltcg.losses,
});

const calculateOpportunityScore = (h) =>
  Math.max(0, -h.stcg.gain) + Math.max(0, -h.ltcg.gain);

const Dashboard = () => {
  const [selected, setSelected] = useState([]);
  const [preHarvest, setPreHarvest] = useState({});
  const [postHarvest, setPostHarvest] = useState({});

  useEffect(() => {
    const reducer = (key, isProfit) =>
      holdingsData.reduce(
        (sum, h) => sum + (isProfit ? Math.max(0, h[key].gain) : Math.max(0, -h[key].gain)),
        0
      );

    const initial = calculateNetGains({
      stcg: { profits: reducer('stcg', true), losses: reducer('stcg', false) },
      ltcg: { profits: reducer('ltcg', true), losses: reducer('ltcg', false) },
    });

    setPreHarvest(initial);
    setPostHarvest(initial);
  }, []);

  const toggleSelect = (coin) => {
    const updated = selected.includes(coin)
      ? selected.filter((c) => c !== coin)
      : [...selected, coin];
    setSelected(updated);

    const selectedHoldings = holdingsData.filter((h) => updated.includes(h.coin));

    const reducer = (key, isProfit) =>
      selectedHoldings.reduce(
        (sum, h) => sum + (isProfit ? Math.max(0, h[key].gain) : Math.max(0, -h[key].gain)),
        0
      );

    const updatedPost = calculateNetGains({
      stcg: { profits: reducer('stcg', true), losses: reducer('stcg', false) },
      ltcg: { profits: reducer('ltcg', true), losses: reducer('ltcg', false) },
    });

    setPostHarvest(updatedPost);
  };

  return (
    <div className="p-6 bg-gradient-to-br from-gray-900 to-gray-800 min-h-screen text-white">
      <div className="text-center mb-6 text-yellow-400 flex items-center justify-center gap-2 text-xl font-medium">
        <Sparkles size={24} />
        AI Insights: Harvest losses wisely to reduce taxes.
      </div>

      <div className="flex flex-col sm:flex-row gap-6 mb-6">
        {[
          { title: 'Pre-Harvesting', data: preHarvest, color: 'bg-gray-700' },
          { title: 'After-Harvesting', data: postHarvest, color: 'bg-blue-600' },
        ].map(({ title, data, color }, i) => (
          <div key={title} className={`w-full sm:w-1/2 p-4 rounded-xl shadow-xl ${color}`}>
            <h2 className="text-lg font-bold mb-2">{title}</h2>
            <p>Short-term: ₹{data.stcgNet}</p>
            <p>Long-term: ₹{data.ltcgNet}</p>
            <p>Realised Gains: ₹{data.realised}</p>
            {i === 1 && preHarvest.realised > postHarvest.realised && (
              <div className="mt-2 bg-green-500 text-black text-center p-2 rounded font-semibold">
                Tax Saved: ₹{preHarvest.realised - postHarvest.realised}
              </div>
            )}
          </div>
        ))}
      </div>

      <div className="overflow-x-auto">
        <table className="w-full text-sm text-left">
          <thead className="text-xs text-gray-300 uppercase bg-gray-700">
            <tr>
              <th className="px-4 py-2">Select</th>
              <th className="px-4 py-2">Asset</th>
              <th className="px-4 py-2">Holdings</th>
              <th className="px-4 py-2">Avg Buy</th>
              <th className="px-4 py-2">Current</th>
              <th className="px-4 py-2">ST Gain</th>
              <th className="px-4 py-2">LT Gain</th>
              <th className="px-4 py-2">Opportunity</th>
            </tr>
          </thead>
          <tbody>
            {holdingsData.map((h) => {
              const selectedRow = selected.includes(h.coin);
              return (
                <tr
                  key={h.coin}
                  className={`border-b border-gray-700 hover:bg-gray-700 ${
                    selectedRow ? 'bg-gray-700' : 'bg-gray-800'
                  }`}
                >
                  <td className="px-4 py-2">
                    <input
                      type="checkbox"
                      checked={selectedRow}
                      onChange={() => toggleSelect(h.coin)}
                    />
                  </td>
                  <td className="px-4 py-2 flex items-center gap-2">
                    <img src={h.logo} alt={h.coin} className="w-5 h-5" />
                    {h.coinName}
                  </td>
                  <td className="px-4 py-2">{h.totalHoldings}</td>
                  <td className="px-4 py-2">₹{h.averageBuyPrice}</td>
                  <td className="px-4 py-2">₹{h.currentPrice}</td>
                  <td
                    className={`px-4 py-2 ${
                      h.stcg.gain >= 0 ? 'text-green-400' : 'text-red-400'
                    }`}
                  >
                    ₹{h.stcg.gain}
                  </td>
                  <td
                    className={`px-4 py-2 ${
                      h.ltcg.gain >= 0 ? 'text-green-400' : 'text-red-400'
                    }`}
                  >
                    ₹{h.ltcg.gain}
                  </td>
                  <td className="px-4 py-2 font-bold text-yellow-400">
                    ₹{calculateOpportunityScore(h)}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Dashboard;
