import React, { useState, useEffect } from 'react';
import { Sparkles } from 'lucide-react';

const holdingsData = [
  {
    coin: 'btc',
    coinName: 'Bitcoin',
    logo: 'https://cryptologos.cc/logos/bitcoin-btc-logo.png',
    totalHoldings: 2.5,
    averageBuyPrice: 30000,
    currentPrice: 35000,
    stcg: { gain: 10000, balance: 1.5 },
    ltcg: { gain: -5000, balance: 1 },
  },
  {
    coin: 'eth',
    coinName: 'Ethereum',
    logo: 'https://cryptologos.cc/logos/ethereum-eth-logo.png',
    totalHoldings: 5,
    averageBuyPrice: 1500,
    currentPrice: 1200,
    stcg: { gain: -3000, balance: 2 },
    ltcg: { gain: 2000, balance: 3 },
  },
];

const calculateNetGains = (gains) => {
  const stcgNet = gains.stcg.profits - gains.stcg.losses;
  const ltcgNet = gains.ltcg.profits - gains.ltcg.losses;
  const realised = stcgNet + ltcgNet;
  return { stcgNet, ltcgNet, realised };
};

const calculateOpportunityScore = (h) => {
  const st = h.stcg.gain < 0 ? Math.abs(h.stcg.gain) : 0;
  const lt = h.ltcg.gain < 0 ? Math.abs(h.ltcg.gain) : 0;
  return st + lt;
};

const Dashboard = () => {
  const [selected, setSelected] = useState([]);
  const [preHarvest, setPreHarvest] = useState({});
  const [postHarvest, setPostHarvest] = useState({});

  useEffect(() => {
    const stcgProfits = holdingsData.reduce((sum, h) => sum + (h.stcg.gain > 0 ? h.stcg.gain : 0), 0);
    const stcgLosses = holdingsData.reduce((sum, h) => sum + (h.stcg.gain < 0 ? -h.stcg.gain : 0), 0);
    const ltcgProfits = holdingsData.reduce((sum, h) => sum + (h.ltcg.gain > 0 ? h.ltcg.gain : 0), 0);
    const ltcgLosses = holdingsData.reduce((sum, h) => sum + (h.ltcg.gain < 0 ? -h.ltcg.gain : 0), 0);

    const initial = calculateNetGains({
      stcg: { profits: stcgProfits, losses: stcgLosses },
      ltcg: { profits: ltcgProfits, losses: ltcgLosses },
    });

    setPreHarvest(initial);
    setPostHarvest(initial);
  }, []);

  const toggleSelect = (coin) => {
    const updated = selected.includes(coin)
      ? selected.filter((c) => c !== coin)
      : [...selected, coin];
    setSelected(updated);

    const selectedHoldings = holdingsData.filter((h) => updated.includes(h.coin));
    const stcgProfits = selectedHoldings.reduce((sum, h) => sum + (h.stcg.gain > 0 ? h.stcg.gain : 0), 0);
    const stcgLosses = selectedHoldings.reduce((sum, h) => sum + (h.stcg.gain < 0 ? -h.stcg.gain : 0), 0);
    const ltcgProfits = selectedHoldings.reduce((sum, h) => sum + (h.ltcg.gain > 0 ? h.ltcg.gain : 0), 0);
    const ltcgLosses = selectedHoldings.reduce((sum, h) => sum + (h.ltcg.gain < 0 ? -h.ltcg.gain : 0), 0);

    const updatedPost = calculateNetGains({
      stcg: { profits: stcgProfits, losses: stcgLosses },
      ltcg: { profits: ltcgProfits, losses: ltcgLosses },
    });

    setPostHarvest(updatedPost);
  };

  return (
    <div className="p-6 bg-gradient-to-br from-gray-900 to-gray-800 min-h-screen text-white">
      <div className="text-center mb-4 text-yellow-400 flex items-center justify-center gap-2 text-xl">
        <Sparkles size={24} /> AI Insights: Strategically harvesting losses can reduce your tax bill. Choose wisely.
      </div>

      <div className="flex gap-6 mb-6">
        <div className="w-1/2 bg-gray-700 p-4 rounded-xl shadow-xl">
          <h2 className="text-lg font-bold mb-2">Pre-Harvesting</h2>
          <p>Short-term: ₹{preHarvest.stcgNet}</p>
          <p>Long-term: ₹{preHarvest.ltcgNet}</p>
          <p>Realised Capital Gains: ₹{preHarvest.realised}</p>
        </div>
        <div className="w-1/2 bg-blue-600 p-4 rounded-xl shadow-xl">
          <h2 className="text-lg font-bold mb-2">After-Harvesting</h2>
          <p>Short-term: ₹{postHarvest.stcgNet}</p>
          <p>Long-term: ₹{postHarvest.ltcgNet}</p>
          <p>Realised Capital Gains: ₹{postHarvest.realised}</p>
          {preHarvest.realised > postHarvest.realised && (
            <div className="mt-2 bg-green-500 text-black text-center p-2 rounded font-semibold">
              Tax Saved: ₹{preHarvest.realised - postHarvest.realised}
            </div>
          )}
        </div>
      </div>

      <table className="w-full text-sm text-left">
        <thead className="text-xs text-gray-300 uppercase bg-gray-700">
          <tr>
            <th className="px-4 py-2">Select</th>
            <th className="px-4 py-2">Asset</th>
            <th className="px-4 py-2">Holdings</th>
            <th className="px-4 py-2">Avg Buy Price</th>
            <th className="px-4 py-2">Current Price</th>
            <th className="px-4 py-2">ST Gain</th>
            <th className="px-4 py-2">LT Gain</th>
            <th className="px-4 py-2">Opportunity Score</th>
          </tr>
        </thead>
        <tbody>
          {holdingsData.map((h) => (
            <tr key={h.coin} className="bg-gray-800 border-b border-gray-700 hover:bg-gray-700">
              <td className="px-4 py-2">
                <input
                  type="checkbox"
                  checked={selected.includes(h.coin)}
                  onChange={() => toggleSelect(h.coin)}
                />
              </td>
              <td className="px-4 py-2 flex items-center gap-2">
                <img src={h.logo} alt={h.coin} className="w-5 h-5" />
                {h.coinName}
              </td>
              <td className="px-4 py-2">{h.totalHoldings}</td>
              <td className="px-4 py-2">₹{h.averageBuyPrice}</td>
              <td className="px-4 py-2">₹{h.currentPrice}</td>
              <td className="px-4 py-2 text-green-400">₹{h.stcg.gain}</td>
              <td className="px-4 py-2 text-green-400">₹{h.ltcg.gain}</td>
              <td className="px-4 py-2 font-bold text-yellow-400">₹{calculateOpportunityScore(h)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
